-- #设置客户端连接服务器端的编码
set names utf8;
-- #丢弃数据库，如果存在
drop database if exists tpp;
-- #创建新的数据库，设置存储的编码
create database tpp charset=utf8;
-- #进入数据库
use tpp;
-- #创建城市表
CREATE TABLE cities(
    cityid int primary key auto_increment,
    word varchar(16),
    cityname varchar(32),
    ishot boolean
);

-- #创建影片表
CREATE TABLE movie_detail(
    did int primary key auto_increment,
    title varchar(32),
    title_eng varchar(32),
    long_time int,
    play_time date,
    place varchar(32),
    tuijian varchar(32),
    tag varchar(32),
    jieshao varchar(1024),
    fmpic varchar(512)
);


CREATE TABLE movie_actor(
    aid int primary key auto_increment,
    aname varchar(32),
    eng_name varchar(32),
    birth bigint,
    act_add varchar(32),
    act_pic varchar(64),
    jianjie varchar(1024),
    detailid int,
    ac_type varchar(32),
    foreign key(detailid) references movie_detail(did)
);


CREATE TABLE movie_video(
    vid int primary key auto_increment,
    title varchar(32),
    addr varchar(128),
    vpic varchar(32),
    long_time int,
    detailid int,
    foreign key(detailid) references movie_detail(did)
);

CREATE TABLE user(
    u_id int primary key auto_increment,
    uname varchar(32),
    upic varchar(128),
    uphone varchar(16),
    upwd varchar(16)
);

CREATE TABLE comment(
    cid int primary key auto_increment,
    title varchar(512),
    score smallint,
    xsdz smallint,
    pltime datetime,
    detailid int,
    userid int,
    foreign key(detailid) references movie_detail(did),
    foreign key(userid) references user(u_id)
);

CREATE TABLE want(
    wid int primary key auto_increment,
    seed varchar(128),
    detailid int,
    userid int,
    foreign key(detailid) references movie_detail(did),
    foreign key(userid) references user(u_id)
);

CREATE TABLE photo(
    pid int primary key auto_increment,
    movie_pic varchar(512),
    picfl varchar(16),
    detailid int,
    foreign key(detailid) references movie_detail(did)
);

CREATE TABLE near(
    nid int primary key auto_increment,
    author_name varchar(32),
    ntitle varchar(32),
    npic varchar(512),
    home varchar(32),
    head_pic varchar(512),
    tag varchar(128),
    detailid int,
    foreign key(detailid) references movie_detail(did)
);


CREATE TABLE cinema(
    cinema_id int primary key auto_increment,
    cinema_name varchar(16),
    addr varchar(32),
    cinema_tag varchar(32)
);

CREATE TABLE cinema_movie(
    cm_id int primary key auto_increment,
    show_time time,
    yt varchar(32),
    price decimal(8,2),
    yt_type varchar(32),
    cmid int,
    movid int,
    foreign key(cmid) references cinema(cinema_id),
    foreign key(movid) references movie_detail(did)
);
