INSERT INTO `movie_detail`(`did`, `title`, `title_eng`, `long_time`, `play_time`, `place`, `tuijian`, `tag`, `jieshao`, `fmpic`) VALUES (3, '归途追凶', 'null', 0, '2022-05-05', '中国大陆', '0.4', '恐怖/悬疑', '电影《长津湖》以抗美援朝战争第二次战役中的长津湖战役为背景，讲述了一段波澜壮阔的历史：71年前，中国人民志愿军赴朝作战，在极寒严酷环境下，东线作战部队凭着钢铁意志和英勇无畏的战斗精神一路追击，奋勇杀敌，扭转了战场态势，打出了军威国威。', '/img/fengmian/005.png');
INSERT INTO `movie_detail`(`did`, `title`, `title_eng`, `long_time`, `play_time`, `place`, `tuijian`, `tag`, `jieshao`, `fmpic`) VALUES (4, '屋内有人', 'null', 128, '2021-09-02', '中国大陆', '0.6', '恐怖', '女演员安然深夜回家躺在床上之后，发现了一件极其恐惧的事实——床下藏着一黑衣人。黑衣人是个窃贼，看到美丽的安然后又起了色心。一个想营救自己，一个想侵犯对方，安然和黑衣人开始了殊死较量。', '/img/fengmian/004.png');
INSERT INTO `movie_detail`(`did`, `title`, `title_eng`, `long_time`, `play_time`, `place`, `tuijian`, `tag`, `jieshao`, `fmpic`) VALUES (5, '长津湖', 'lake', 176, '2021-09-30', '中国大陆', '0.95', '战争/剧情', '1950年冬天，冰天雪地的长津湖区域，一场美国与中国炼狱般的血战即将拉开序幕。中美双方分别派遣最为精锐的王牌部队出战——中国人民志愿军第9兵团、美军陆战第1师，步兵第7师。', '/img/fengmian/002.png');
INSERT INTO `movie_detail`(`did`, `title`, `title_eng`, `long_time`, `play_time`, `place`, `tuijian`, `tag`, `jieshao`, `fmpic`) VALUES (6, '中国机长', 'null', 111, '2021-12-01', '中国大陆', '0.92', '剧情', '四川航空3U8633航班机组执行航班任务时，在万米高空突遇驾驶舱风挡玻璃爆裂脱落、座舱释压的极端罕见险情。机组成员凭借着极少仍在工作状态的仪器，艰难地进行手动驾驶。座舱释压发生时，乘务组立即执行释压处置程序，指导旅客使用氧气面罩，并训练有素地喊出：“请大家相信我们，相信我们有信心、有能力带领大家安全落地。”生死关头，英雄机组的正确处置，确保了机上全体人员的生命安全，创造了世界民航史上的奇迹 。', '/img/fengmian/003.png');
