INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (63, '郭健', 'Derek Kwdk', 19760101, '中国香港', '/img/yanyuanpic/101.png', '1976年出生于中国香港，香港电影导演、编剧、制作人。2007年，凭借执导的剧情片《野·良犬》获得第28届香港电影金像奖新晋导演。2010年，执导动作片《打擂台》，该片获得第30届香港电影金像奖最佳电影奖 。2013年，与周星驰联合执导古装神话片《西游·降魔篇》  。2014年，凭借执导的灾难片《救火英雄》获得第33届香港电影金像奖最佳导演提名。2015年 ，执导古装爱情片《悟空传》，该片于2017年7月13日在中国大陆上映', 12, '导演');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (64, '雷佳音', 'jiayin Lei', 19830829, '中国辽宁鞍山', '/img/yanyuanpic/102.png', '1983年8月29日出生于辽宁省鞍山市，中国内地影视男演员，毕业于上海戏剧学院表演系。2004年，出演个人首部电视剧《江湖俏佳人》，从而正式进入演艺圈。2006年，他以第一名的成绩考进上海话剧中心。2009年，出演个人首部电影《高考1977》。2011年，获得第15届佐临话剧艺术奖最具潜质新人奖。2012年，凭借动作喜剧电影《黄金大劫案》获得第11届中国长春电影节最佳男主角奖等多个奖项。', 12, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (65, '李现', 'Xian Li', 19911019, '中国湖北荆州', '/img/yanyuanpic/103.png', '1991年10月19日出生于湖北省咸宁市，成长于湖北省荆州市，中国内地影视男演员，毕业于北京电影学院表演系2010级。2011年，出演个人首部电影《万箭穿心》，从而正式进入演艺圈。2012年，主演青春爱情电影《初恋未满》。2013年，出演爱情喜剧电影《玩命试爱》。2014年，出演市奇幻单元《奇妙世纪》中第二集“最长的25米”。2015年，主演青春校园网络剧《睡在我上铺的兄弟》。', 12, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (66, '辛芷蕾', 'Zhilei Xin', 19860408, '中国黑龙江哈尔滨', '/img/yanyuanpic/201.png', '1986年2月28日出生于黑龙江省鹤岗市，中国内地影视女演员，毕业于中央戏剧学院 。2016年2月，主演魔幻现实主义爱情电影《长江图》受到关注，该片入围第66届柏林国际电影节主竞赛单元。2017年7月，出演古装动作电影《绣春刀：修罗战场》；11月，参加浙江卫视演技竞演类励志综艺《演员的诞生》。', 12, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (67, '葛优', 'You Ge', 19570419, '中国北京', '/img/yanyuanpic/104.png', '1957年4月19日出生于北京市，祖籍河北衡水，中国男演员，国家一级演员。1979年，考入中华全国总工会文工团。1985年，出演个人首部电影《盛夏和她的未婚夫》，从而正式出道。1992年，因在电视剧《编辑部的故事》中饰演李冬宝一角获得第10届中国电视金鹰奖最佳男主角奖。1993年，凭借电影《大撒把》获得第13届中国电影金鸡奖最佳男主角奖。1994年，凭借电影《活着》获得第47届戛纳国际电影节最佳男演员奖，成为中国内地首位戛纳影帝。1997年，在冯小刚执导的贺岁片《甲方乙方》中饰演姚远一角。1998年、2002年、2004年三度获得大众电影百花奖最佳男主角奖。', 12, '演员');
