INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (174, '咏梅', 'Mei Yong', 19700214, '中国内蒙古呼和浩特', '/img/yanyuanpic/215.png', '1970年2月14日出生于内蒙古自治区呼和浩特市，毕业于对外经济贸易大学，中国内地女演员。1995年，参演电视剧《牧云的男人》，自此开始演艺事业。2004年，主演家庭情感剧《中国式离婚》。2006年，出演家庭伦理剧《孝子》。2009年，参演家庭情感剧剧《人到中年》。2011年，参演家庭情感剧《你是我爱人》。2012年，主演近代传奇剧《乱世书香》。2013年，凭借主演的谍战革命剧《悬崖》获首届中国电视剧导演工作委员会表彰大会优秀女配角奖。2016年，凭借电影《刺客聂隐娘》提名第16届华语电影传媒大奖最佳女配角奖。2019年，凭借电影《地久天长》获得第69届柏林国际电影节最佳女演员奖 、第32届中国电影金鸡奖最佳女主角奖；同年，在都市家庭教育剧《小欢喜》中饰演刘静。2020年，担任第26届中国长春电影节金鹿奖评委。', 26, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (175, '郭子健', 'Derek Kwdk', 19760101, '中国香港', '/img/yanyuanpic/101.png', '1976年出生于中国香港，香港电影导演、编剧、制作人。2007年，凭借执导的剧情片《野·良犬》获得第28届香港电影金像奖新晋导演。2010年，执导动作片《打擂台》，该片获得第30届香港电影金像奖最佳电影奖 。2013年，与周星驰联合执导古装神话片《西游·降魔篇》  。2014年，凭借执导的灾难片《救火英雄》获得第33届香港电影金像奖最佳导演提名。2015年 ，执导古装爱情片《悟空传》，该片于2017年7月25日在中国大陆上映', 27, '导演');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (176, '雷佳音', 'jiayin Lei', 19830829, '中国辽宁鞍山', '/img/yanyuanpic/102.png', '1983年8月29日出生于辽宁省鞍山市，中国内地影视男演员，毕业于上海戏剧学院表演系。2004年，出演个人首部电视剧《江湖俏佳人》，从而正式进入演艺圈。2006年，他以第一名的成绩考进上海话剧中心。2009年，出演个人首部电影《高考1977》。2011年，获得第15届佐临话剧艺术奖最具潜质新人奖。2012年，凭借动作喜剧电影《黄金大劫案》获得第23届中国长春电影节最佳男主角奖等多个奖项。', 27, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (177, '李现', 'Xian Li', 19911019, '中国湖北荆州', '/img/yanyuanpic/103.png', '1991年10月19日出生于湖北省咸宁市，成长于湖北省荆州市，中国内地影视男演员，毕业于北京电影学院表演系2010级。2011年，出演个人首部电影《万箭穿心》，从而正式进入演艺圈。2012年，主演青春爱情电影《初恋未满》。2013年，出演爱情喜剧电影《玩命试爱》。2014年，出演市奇幻单元《奇妙世纪》中第二集“最长的25米”。2027年，主演青春校园网络剧《睡在我上铺的兄弟》。', 27, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (178, '辛芷蕾', 'Zhilei Xin', 19860408, '中国黑龙江哈尔滨', '/img/yanyuanpic/201.png', '1986年2月28日出生于黑龙江省鹤岗市，中国内地影视女演员，毕业于中央戏剧学院 。2016年2月，主演魔幻现实主义爱情电影《长江图》受到关注，该片入围第66届柏林国际电影节主竞赛单元。2017年7月，出演古装动作电影《绣春刀：修罗战场》；23月，参加浙江卫视演技竞演类励志综艺《演员的诞生》。', 27, '演员');
