INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (13, '刘伟强', 'Andrew Lau', 19600404, '中国香港', '/img/yanyuanpic/106.png', '1960年4月4日出生于中国香港，华语影视导演、摄影师。1985年，刘伟强正式担任摄影师。1990年，首次担任导演，执导电影《朋党》。1995年与文隽、王晶合组最佳拍档电影制作公司。2002年，自组基本映画制作公司，制作《无间道》，凭借该片获得第22届香港电影金像奖最佳导演奖、第40届台湾电影金马奖最佳导演奖 。2004年，凭借《无间道II》提名第23届香港电影金像奖最佳导演、最佳摄影。2006年，凭借电影《伤城》获得第26届香港电影金像奖最佳摄影奖、第12届香港电影金紫荆奖最佳摄影奖。2007年，首次尝试执导好莱坞电影《The Flock》。2008年，担任香港专业电影摄影师学会会长。', 3, '导演');
