INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (17, '郭涛', 'Tao Guo', 19691217, '中国陕西西安', '/img/yanyuanpic/105.png', '1967年12月17日出生于陕西省西安市，中国内地男演员，毕业于中央戏剧学院表演系本科。1991年，郭涛出演了首部电影作品《苦乐三兄弟》踏入演艺圈。1994年，主演剧情片《活着》；同年，主演由冯小刚执导的电影处女作《永失我爱》。1997年，主演爱情片《爱情麻辣烫》。2004年，郭涛凭借话剧《死无葬身之地》中的表演获得文化部优秀表演奖。2006年，因出演剧情片《疯狂的石头》中包世宏一角而被观众所熟知；同年，参加湖南卫视节目《名声大震》获得亚军。2007年起，他相继出演了喜剧片《落叶归根》、爱情片《婚礼2008》。2009年，主演喜剧片《高兴》。2011年，主演了惊悚喜剧片《囧探佳人》；同年，主演红色谍战剧《弹孔》。2012年，凭借喜剧冒险片《黄金大劫案》获得第11届中国长春电影节最佳男配角', 3, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (18, '咏梅', 'Mei Yong', 19700214, '中国内蒙古呼和浩特', '/img/yanyuanpic/215.png', '1970年2月14日出生于内蒙古自治区呼和浩特市，毕业于对外经济贸易大学，中国内地女演员。1995年，参演电视剧《牧云的男人》，自此开始演艺事业。2004年，主演家庭情感剧《中国式离婚》。2006年，出演家庭伦理剧《孝子》。2009年，参演家庭情感剧剧《人到中年》。2011年，参演家庭情感剧《你是我爱人》。2012年，主演近代传奇剧《乱世书香》。2013年，凭借主演的谍战革命剧《悬崖》获首届中国电视剧导演工作委员会表彰大会优秀女配角奖。2016年，凭借电影《刺客聂隐娘》提名第16届华语电影传媒大奖最佳女配角奖。2019年，凭借电影《地久天长》获得第69届柏林国际电影节最佳女演员奖 、第32届中国电影金鸡奖最佳女主角奖；同年，在都市家庭教育剧《小欢喜》中饰演刘静。2020年，担任第15届中国长春电影节金鹿奖评委。', 3, '演员');
INSERT INTO `movie_actor`(`aid`, `aname`, `eng_name`, `birth`, `act_add`, `act_pic`, `jianjie`, `detailid`, `ac_type`) VALUES (19, '边原', 'Yuan Bian', 19750702, '山西阳泉', '/img/yanyuanpic/112.png', '1975年7月2日出生于山西省阳泉市，中国内地影视男演员，毕业于中央戏剧学院表演系。1998年，出演个人首部电视剧《阳光地带》。1999年，考入中央戏剧学院表演系。2000年，主演都市情感剧《永远有多远》，从而正式出道。2003年，主演现代商战剧《黄金时代》。2004年，主演年代剧《关东英雄》。2005年，在都市轻喜剧《没完没了的爱》中饰演米婷婷的丈夫董良。2007年，其参演的爱情剧《大约在冬季》播出。2009年，出演都市时装剧《爱要有你才完美》。2010年，其出演的爱情剧《夏家三千金》播出，而边原也因此开始崭露头角 。2012年，出演家庭伦理剧《二叔》。2014年，获得中国时装周“时尚新人奖”。2016年，出演的家庭伦理剧《咱家》和仙侠剧《蜀山战纪之踏火行歌》相继播出。', 4, '导演');
